import React from 'react';
import { 
  MicIcon, 
  AttachIcon, 
  ThumbsUpIcon, 
  SendIcon 
} from '../assets/icons';
import './ChatInterface.scss';

const ChatInterface = () => {
  return (
    <div className="chat-interface">
      {/* Chat Input Area */}
      <div className="chat-interface__input-area">
        {/* Input Background */}
        <div className="chat-interface__input-background"></div>

        {/* Input Controls */}
        <div className="chat-interface__controls">
          <div className="chat-interface__left-controls">
            <div className="chat-interface__mic-container">
              <MicIcon />
            </div>
            <div className="chat-interface__attach-container">
              <AttachIcon />
            </div>
          </div>

          <div className="chat-interface__right-controls">
            <ThumbsUpIcon />
            <div className="chat-interface__send-button">
              <SendIcon />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
