import React from 'react';

const ChatItem = ({ title, tag, tagColor, timeAgo, updated }) => {
  return (
    <div className="bg-white border border-gray-200 rounded p-2 hover:bg-gray-50 transition-colors">
      {/* Header */}
      <div className="flex items-start justify-between mb-1">
        <span className="text-sm text-ella-light-gray flex-1 truncate pr-2">{title}</span>
        <div className="flex items-center gap-2">
          <span className="text-xs text-transparent">{timeAgo}</span>
          <button className="w-2 h-2 text-gray-400 hover:text-gray-600">
            <svg fill="currentColor" viewBox="0 0 24 24">
              <path d="M6 10c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm12 0c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm-6 0c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2z"/>
            </svg>
          </button>
        </div>
      </div>

      {/* Footer */}
      <div className="flex items-center gap-3">
        <span className={`text-xs px-2 py-1 rounded ${tagColor}`}>
          {tag}
        </span>
        <span className="text-xs text-gray-500 italic">{updated}</span>
      </div>
    </div>
  );
};

export default ChatItem;
