import React from 'react';

const ChatItem = ({ title, tag, tagColor, tagBg, timeAgo, updated }) => {
  return (
    <div className="bg-white border border-ella-chat-border rounded" style={{width: '272.99px'}}>
      <div className="px-2 py-1 flex flex-col gap-0.5">
        {/* Header */}
        <div className="flex items-stretch gap-3">
          <span className="text-sm text-ella-light-gray flex-1 leading-5 flex items-center" style={{width: '180.99px'}}>{title}</span>
          <span className="text-xs text-transparent leading-4 flex items-center mt-0.5 w-10">{timeAgo}</span>
          <button className="w-2.5 h-0.5 text-ella-gray hover:text-gray-600 mt-2">
            <svg fill="currentColor" viewBox="0 0 24 24">
              <path d="M6 10c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm12 0c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2zm-6 0c-1.1 0-2 .9-2 2s.9 2 2 2 2-.9 2-2-.9-2-2-2z"/>
            </svg>
          </button>
        </div>

        {/* Footer */}
        <div className="flex items-stretch gap-3" style={{width: '257px'}}>
          <span className={`text-xs px-1 rounded mt-0.5 mb-0.5 flex items-center justify-center ${tagColor}`} 
                style={{
                  width: tag === "Ellaments" ? '54px' : '51px', 
                  minHeight: '16px',
                  backgroundColor: tagBg,
                  fontSize: '10px',
                  lineHeight: '16px',
                  color: tag === "Ellaments" ? '#057B14' : '#1757C1'
                }}>
            {tag}
          </span>
          <span className="text-xs text-ella-gray italic leading-5 flex items-center font-light" style={{width: '120px'}}>{updated}</span>
        </div>
      </div>
    </div>
  );
};

export default ChatItem;
