import React, { useState, useRef, useEffect } from 'react';
import {
  EllaLogoIcon,
  EllaBrandIcon,
  ChevronRightIcon,
  ChevronDownIcon,
  ShareIcon,
  SettingsIcon,
  UserIcon,
  MoreIcon,
  ProfileIcon
} from '../assets/icons';
import { useTheme } from '../context/ThemeContext';
import '../styles/Header.scss';

const Header = () => {
  const { currentTheme, themes, setTheme } = useTheme();
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const handleThemeSelect = (theme) => {
    setTheme(theme);
    setIsDropdownOpen(false);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="header close-ella-search">
      <div className="header__left">
        <div className="header__logo-section">
          <EllaLogoIcon />
          <EllaBrandIcon />
          <div className="header__workspace-text">Workspace</div>
          <ChevronRightIcon />
        </div>
      </div>

      <div className="header__center">
        <div className="theme-dropdown" ref={dropdownRef}>
          <button 
            className="theme-dropdown__trigger"
            onClick={() => setIsDropdownOpen(!isDropdownOpen)}
          >
            <span className="theme-dropdown__text">{currentTheme}</span>
            <ChevronDownIcon />
          </button>

          {isDropdownOpen && (
            <div className="theme-dropdown__menu">
              {themes.map((theme) => (
                <div
                  key={theme}
                  className={`theme-dropdown__item ${currentTheme === theme ? 'theme-dropdown__item--selected' : ''}`}
                  onClick={() => handleThemeSelect(theme)}
                >
                  {theme}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      <div className="header__right">
        <div className="header__actions">
          <ShareIcon />
          <SettingsIcon />
          <UserIcon />
          <MoreIcon />
          <ProfileIcon />
        </div>
      </div>
    </div>
  );
};

export default Header;
