import React, { useState } from 'react';
import { 
  AttachIcon, 
  ThumbsUpIcon, 
  SendIcon 
} from '../assets/icons';
import '../styles/ChatInterface.scss';

const ChatInterface = () => {
  const [inputValue, setInputValue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim()) {
      console.log('Sending message:', inputValue);
      setInputValue('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="chat-interface">
      {/* Chat Input Area */}
      <div className="chat-interface__input-area">
        {/* Input Field */}
        <div className="chat-interface__input-container">
          <textarea
            className="chat-interface__input-field"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            rows={1}
          />
        </div>

        {/* Input Controls */}
        <div className="chat-interface__controls">
          <div className="chat-interface__left-controls">
            <div className="chat-interface__mic-container">
              <svg xmlns="http://www.w3.org/2000/svg" width="35.56" height="35.56" viewBox="0 0 90 90" className="mic-icon">
                <g style={{stroke: 'none', strokeWidth: 0, strokeDasharray: 'none', strokeLinecap: 'butt', strokeLinejoin: 'miter', strokeMiterlimit: 10, fill: 'none', fillRule: 'nonzero', opacity: 1}} transform="translate(1.4065934065934016 1.4065934065934016) scale(2.81 2.81)">
                  <path d="M 45 68.988 c -1.104 0 -2 -0.896 -2 -2 V 23.011 c 0 -1.104 0.896 -2 2 -2 s 2 0.896 2 2 v 43.977 C 47 68.093 46.104 68.988 45 68.988 z" style={{stroke: 'none', strokeWidth: 1, strokeDasharray: 'none', strokeLinecap: 'butt', strokeLinejoin: 'miter', strokeMiterlimit: 10, fill: 'rgb(0,0,0)', fillRule: 'nonzero', opacity: 1}} transform="matrix(1 0 0 1 0 0)" strokeLinecap="round"/>
                  <path d="M 66.988 47 H 23.011 c -1.104 0 -2 -0.896 -2 -2 s 0.896 -2 2 -2 h 43.977 c 1.104 0 2 0.896 2 2 S 68.093 47 66.988 47 z" style={{stroke: 'none', strokeWidth: 1, strokeDasharray: 'none', strokeLinecap: 'butt', strokeLinejoin: 'miter', strokeMiterlimit: 10, fill: 'rgb(0,0,0)', fillRule: 'nonzero', opacity: 1}} transform="matrix(1 0 0 1 0 0)" strokeLinecap="round"/>
                  <path d="M 73.952 90 H 16.048 C 7.199 90 0 82.801 0 73.952 V 16.048 C 0 7.199 7.199 0 16.048 0 h 57.904 C 82.801 0 90 7.199 90 16.048 v 57.904 C 90 82.801 82.801 90 73.952 90 z M 16.048 4 C 9.405 4 4 9.405 4 16.048 v 57.904 C 4 80.596 9.405 86 16.048 86 h 57.904 C 80.596 86 86 80.596 86 73.952 V 16.048 C 86 9.405 80.596 4 73.952 4 H 16.048 z" style={{stroke: 'none', strokeWidth: 1, strokeDasharray: 'none', strokeLinecap: 'butt', strokeLinejoin: 'miter', strokeMiterlimit: 10, fill: 'rgb(0,0,0)', fillRule: 'nonzero', opacity: 1}} transform="matrix(1 0 0 1 0 0)" strokeLinecap="round"/>
                </g>
              </svg>
            </div>
            <div className="chat-interface__attach-container">
              <AttachIcon />
            </div>
          </div>

          <div className="chat-interface__right-controls">
            <ThumbsUpIcon />
            <div 
              className="chat-interface__send-button"
              onClick={handleSubmit}
            >
              <SendIcon />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
