import React from 'react';

const ChatInterface = () => {
  return (
    <div className="flex-1 flex items-center justify-center" style={{padding: '362.5px 199px 356.5px 199px'}}>
      <div className="bg-white rounded-lg flex flex-col items-stretch flex-grow pb-0.5">
        {/* Input Area */}
        <div className="bg-ella-input-bg border border-ella-input-border rounded-t-lg" 
             style={{
               width: '759px', 
               height: '91.87px',
               borderWidth: '1.5px',
               borderBottomWidth: '1.5px'
             }}>
        </div>

        {/* Action Bar */}
        <div className="bg-ella-input-border border-t border-white rounded-b-lg p-2.5 flex items-center justify-between">
          {/* Left Actions */}
          <div className="flex items-center gap-4">
            {/* Microphone Button */}
            <button className="overflow-hidden flex items-center justify-center" style={{padding: '0.2px 0.24px 0.25px 0.2px'}}>
              <div className="w-9 h-9 text-gray-300">
                <svg fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 14c1.66 0 2.99-1.34 2.99-3L15 5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3zm5.3-3c0 3-2.54 5.1-5.3 5.1S6.7 14 6.7 11H5c0 3.41 2.72 6.23 6 6.72V21h2v-3.28c3.28-.48 6-3.3 6-6.72h-1.7z"/>
                </svg>
              </div>
            </button>

            {/* Attachment Button */}
            <button className="bg-transparent rounded-lg flex items-center justify-center mb-0.5 w-9" style={{padding: '7.81px 9px'}}>
              <div className="w-5 h-5 text-gray-300">
                <svg fill="currentColor" viewBox="0 0 24 24">
                  <path d="M16.5 6v11.5c0 2.21-1.79 4-4 4s-4-1.79-4-4V5c0-1.38 1.12-2.5 2.5-2.5s2.5 1.12 2.5 2.5v10.5c0 .55-.45 1-1 1s-1-.45-1-1V6H10v9.5c0 1.38 1.12 2.5 2.5 2.5s2.5-1.12 2.5-2.5V5c0-2.21-1.79-4-4-4S7 2.79 7 5v12.5c0 3.04 2.46 5.5 5.5 5.5s5.5-2.46 5.5-5.5V6h-1.5z"/>
                </svg>
              </div>
            </button>
          </div>

          {/* Right Actions */}
          <div className="flex items-center justify-between" style={{width: '78.02px'}}>
            {/* Share Button */}
            <button className="w-4.5 h-4.5 text-gray-300 hover:text-gray-500 transition-colors" style={{marginTop: '8.97px'}}>
              <svg fill="currentColor" viewBox="0 0 24 24">
                <path d="M18 16.08c-.76 0-1.44.3-1.96.77L8.91 12.7c.05-.23.09-.46.09-.7s-.04-.47-.09-.7l7.05-4.11c.54.5 1.25.81 2.04.81 1.66 0 3-1.34 3-3s-1.34-3-3-3-3 1.34-3 3c0 .24.04.47.09.7L8.04 9.81C7.5 9.31 6.79 9 6 9c-1.66 0-3 1.34-3 3s1.34 3 3 3c.79 0 1.5-.31 2.04-.81l7.12 4.16c-.05.21-.08.43-.08.65 0 1.61 1.31 2.92 2.92 2.92 1.61 0 2.92-1.31 2.92-2.92s-1.31-2.92-2.92-2.92z"/>
              </svg>
            </button>

            {/* Send Button */}
            <button className="bg-blue-600 hover:bg-blue-700 text-white rounded flex items-center justify-center w-9 p-2.5">
              <div className="w-4.5 h-4.5">
                <svg fill="currentColor" viewBox="0 0 24 24">
                  <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
              </div>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
